import React from "react";
import { MapContainer, TileLayer, FeatureGroup } from "react-leaflet";
import { EditControl } from "react-leaflet-draw";
import axios from "axios";

import "leaflet/dist/leaflet.css";
import "leaflet-draw/dist/leaflet.draw.css";

const App = () => {
  const onCreated = async (e) => {
    const geometry = e.layer.toGeoJSON().geometry;

    try {
      const response = await axios.post("http://localhost:8000/predict/", {
        geometry: geometry,
      });

      const { ndvi, rekolte } = response.data;
      alert(`NDVI: ${ndvi}, Tahmini Rekolte: ${rekolte} kg/ha`);
    } catch (error) {
      alert("Tahmin alınamadı: " + error.message);
    }
  };

  return (
    <div style={{ height: "100vh", width: "100%" }}>
      <h1 style={{ textAlign: "center" }}>Tubikal çok seviyorum!!!!</h1>
      <MapContainer center={[39.93, 32.85]} zoom={13} style={{ height: "100%", width: "100%" }}>
        <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
        <FeatureGroup>
          <EditControl
            position="topright"
            draw={{
              polygon: true,
              polyline: false,
              rectangle: true,
              circle: false,
              marker: false,
              circlemarker: false,
            }}
            onCreated={onCreated}
          />
        </FeatureGroup>
      </MapContainer>
    </div>
  );
};

export default App;
